import React, {useState, useEffect} from 'react';
import Panel from './Panel';
import './PanelList.css';


const PanelList = ({ pokedex }) => {
    const [cardIndex, setCardIndex] = useState(null);
    const [cardArray, setCardArray] = useState([])
    const [panelPick, setPanelPick] = useState(null);


    useEffect(() => {
        setPanelPick(panelComp[cardIndex]);

        addCardSelected();

    },[cardIndex]);


    const panelComp = pokedex.map((pokemon, i) => {
        return <Panel 
            onChange={num => setCardIndex(num)} 
            panelId={i} 
            id={pokedex[i].id} 
            name={pokedex[i].name} 
            types={pokedex[i].types}
            />
    })

    const addCardSelected = event => {
        if (panelPick !== null && cardArray.length < 6) {
            setCardArray(prev => [...prev, panelPick])
        }
        console.log(cardArray);
    }


    return (
        <div>
            <div>
                <div className='emptyBox' id='Block1'>{panelPick}</div>
                <div className='emptyBox' id='Block2'></div>
                <div className='emptyBox' id='Block3'></div>
                <div className='emptyBox' id='Block4'></div>
                <div className='emptyBox' id='Block5'></div>
                <div className='emptyBox' id='Block6'></div>
            </div>
            <div>
                {panelComp}
            </div>
        </div>
    );
}

export default PanelList;
