import React, { Component } from 'react';
import PanelList from './PanelList';
import SearchBox from './SearchBox';
import Dropdown  from './Dropdown';
import gameVers from "./GameVers";

    
class Appapi extends Component {   
    constructor() {
        super()
        this.state = {
            pokedex: [],
            searchfield: '',
            gameVersion: 'https://pokeapi.co/api/v2/pokemon?limit=20',
            value: 'null'
        }
    }

    gameVerChange = (event) => {
       gameVers.forEach(array => {
            if (array.id === event){
                this.setState({gameVersion: array.url})
            }
        })

    }

    componentDidMount() {
        fetch(this.state.gameVersion)
            .then(response=> response.json())
            .then(data => {
                let results = data.results;
                let promisesArray = results.map(result => {
                    return fetch(result.url).then(response => response.json());
                })
                return Promise.all(promisesArray);
            }).then((data) => this.setState({pokedex: data}));
    }

    onSearchChange = (event) => {
        this.setState({searchfield: event.target.value})
    }

    render() {
        const filteredPokemon = this.state.pokedex.filter(pokemon => {
            return pokemon.name.toLowerCase().includes(this.state.searchfield.toLowerCase());
        })


        return (
            <div>
                <h1>Pokemon Type Strength</h1>
                <p>Select your game and pokemon to see what types they can beat</p>
                <SearchBox searchChange={this.onSearchChange}/>
                <div style={{width: 250}}>
                    <Dropdown 
                        items={gameVers} 
                        prompt='Select Game...' 
                        value={value} 
                        onChange={val => this.setState({value: val})} />
                </div>
                <PanelList pokedex={filteredPokemon}/>
            </div>
            );
    }
}

export default Appapi;
