import React, { useEffect, useState } from 'react';
import PanelList from './PanelList';
import SearchBox from './SearchBox';
import Dropdown  from './Dropdown';
import gameVers from "./GameVers";



function Appapi() {   

    const [pokedex, setPokedex] = useState([]);
    const [searchfield, setSearchField] = useState('');
    const [gameVersion, setGameVersion] = useState('https://pokeapi.co/api/v2/pokemon?limit=20');
    const [value, setvalue] = useState(null);

    const onGameVerChange = (event) => {
       setGameVersion(event);
       console.log(event);
    }

    useEffect(() => {
        async function fetchData() {
            const res = await fetch(gameVersion);
            res
                .json()
                .then(data => {
                    let results = data.results;
                    let promisesArray = results.map(result => {
                        return fetch(result.url).then(response => response.json());
                    })
                    return Promise.all(promisesArray);})
                .then((data) => setPokedex(data));
        }
        fetchData();
            
    });
    

    const onSearchChange = (event) => {
        setSearchField(event.target.value);
        console.log(gameVersion);
    }

        const filteredPokemon = pokedex.filter(pokemon => {
            return pokemon.name.toLowerCase().includes(searchfield.toLowerCase());
        })

        return (
            <div>
                <h1>Pokemon Type Strength</h1>
                <p>Select your game and pokemon to see what types they can beat</p>
                <SearchBox searchChange={onSearchChange}/>
                <div style={{width: 250}}>
                    <Dropdown 
                        options={gameVers} 
                        prompt='Select Game...' 
                        value={value} 
                        onChange={val => setvalue(val)}
                        gameVerChange={onGameVerChange} />
                </div>
                <PanelList pokedex={filteredPokemon}/>
            </div>
            );
    
}

export default Appapi;


