import React, { useState } from 'react';
// import './Dropdown.css'


function Dropdown({items}){

    return(
        <div className='dropdown'>
            <div className='control'>
                <div className='selected-value'>Select Game..</div>
                <div className='arrow'/>
            </div>
            <div className='options'>
            {
                items.map((item) => (
                    <div className='option'>{item.value}</div>
                ))} 
            </div>
        </div>
        
    )
}


export default Dropdown;
